import React, { useState } from 'react';
import { fnGetDateInfo } from '../../js/dday';

const CompListItem = ({ item }) => {
  const [_remainDays, _setRemainDays] = useState(0)
  const [_remainHours, _setRemainHours] = useState(0)
  const [_remainMinutes, _setRemainMinutes] = useState(0)
  const [_remainSecons, _setRemainSecons] = useState(0)

  const { title, Dday } = item//item이란 props로 내려받은 객체{id, Dday}를 구조분해 
  const { day, year, month, date, ap, hour, min, sec, timeStamp } = fnGetDateInfo(Dday)

  return (
    <li>
      <div className='meta'>
        <h3>{title}</h3>
        <p>
          <time className='date'>
            <em>{year}</em>-
            <em>{month}</em>-
            <em>{date}</em>-
            <small>{day}</small>
          </time>
          <time className='time'>
            <small>{ap}</small>
            <em>{hour}</em>:
            <em>{min}</em>:
            <em>{sec}</em>
          </time>
        </p>
      </div>
      <div>
        {몇} 일
        {몇} 시간
        {몇} 분
        {몇} 초
      </div>
    </li>
  );
};

export default CompListItem;
