import React, { createContext, useState } from 'react';
import CompForm from './CompForm';
import CompResult from './CompResult';
import { fnSetObj } from '../../js/bmi';

export const BMIContext = createContext()

const CompBmi = () => {
  const [_active, _setActive] = useState('')
  const [_gender, _setGender] = useState()
  const [_cm, _setCm] = useState() 
  const [_kg, _setKg] = useState()
  const [_chkObj, _setChkObj] = useState(fnSetObj(0)) /* 1이나 2를 입력하면 male이나 female이 눌려진 상태가 디폴트로 설정됨 */
  const [_bmiObj, _setBmiObj] = useState()


  return (
    <BMIContext.Provider value={{
      _active,_setActive,
      _gender,_setGender,
      _cm,_setCm,
      _kg, _setKg,
      _chkObj, _setChkObj,
      }}>
      <section className='bmi'>
        <h2>bmi</h2>
        <CompForm />
        <CompResult />
      </section>
    </BMIContext.Provider>
  );
};

export default CompBmi;
