import React, { useContext } from 'react';
import { CalcContext } from './CompCalc';
import { fnSetOutput } from '../../js/Btns'

const CompBtn = ({data}) => {
const {_setOutput,_output} =useContext(CalcContext)
  const {type,char,id} = data

 const fnClickHandler = function(){
  _setOutput(fnSetOutput(_output,char,type))  
 }

  return (
    <li>
      <button onClick={fnClickHandler}>{char}</button>
    </li>
  );
};

export default CompBtn;
