import React, { useContext, useState } from 'react';
import { DDayContext } from './CompDday';

const CompFilterForm = ({ _filterActive , _setFilterActive}) => {

  const {
    _ddayArr, _setDdayArr,
    _ddayOutputArr, _setDdayOutputArr,
    _searchText,  }
    = useContext(DDayContext)
  


  const fnSortHandler = function (e) {
    const sortType = e.currentTarget.value
    let ddayArrCopy = [..._ddayArr]
    let sortedArr
    if (sortType === 'latest') { // 최신등록순
      sortedArr = [..._ddayArr]
    } else if (sortType === 'time') { // 시간정렬
      sortedArr = [..._ddayArr].sort((a, b) => {
        if (a.Dday > b.Dday) {
          return 1
        } else if (a.Dday < b.Dday) {
          return -1
        } else {
          return 0
        }
      })
    } else if (sortType === 'title') {
      sortedArr = [..._ddayArr].sort((a, b) => {
        if (a.title.toLowerCase() > b.title.toLowerCase()) {
          return 1
        } else if (a.title.toLowerCase() < b.title.toLowerCase()) {
          return -1
        } else {
          return 0
        }
      }) // sort end
    } // if sortType

    _setDdayOutputArr(sortedArr) // 정렬된걸로 출력을 바꾸겠다.
    _setFilterActive('')
    document.querySelector(`.filter-btn`).classList.remove(`active`)
  } // fn

  const fnSearchHandler = function(e){
    _setSetchText(e.target.value)
  } // fnSearchHandler

  return (
    <form onSubmit={(e) => { e.preventDefault() ; _setSetchText('') }} className={`filter-form ${_filterActive}`} >
      <fieldset>
        <legend><i className="fa-solid fa-list"></i> 정렬</legend>
        <button onClick={fnSortHandler} value='latest'>최신등록순</button>
        <button onClick={fnSortHandler} value='time'>D-day시간</button>
        <button onClick={fnSortHandler} value='title'>D-day제목</button>
      </fieldset>
      <fieldset>
        <legend><i class="fa-solid fa-magnifying-glass"></i> 검색</legend>
        <input value={_searchText} onInput={fnSearchHandler} type="text" />
      </fieldset>
    </form>
  );
};

export default CompFilterForm;
