import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { fnGetTheme } from '../../js/compMenu';

const CompMenu = () => {
  const [_theme, _setTheme] = useState({ 1: false, 2: false, 3: false })
  
  const fnChangeHandler = function(e){
  let n = e.target.value
  let obj = { 1: false, 2: false, 3: false } /* 초기 값을 다 꺼버린다 */
  obj[n] = true /* 변수로 키 값을 대체한다 obj의 n번째만 true 로 바꾸겠다 */
  _setTheme(obj)
  }//fnChangeHandler

  useEffect(() => {
    _setTheme(fnGetTheme())
  }, [])


  return (

    <section className='menu'>

      <h1>Neumorphi_Calc</h1>

      <form className="theme">
        <fieldset>
          <legend><i className="fa-solid fa-palette"></i>  Choice App Theme</legend>
          <div>
            <input onChange={fnChangeHandler} defaultValue='1' checked={_theme['1']} id="radio1" type="radio" name="theme" />
            <label htmlFor="radio1"></label>
            <input onChange={fnChangeHandler} defaultValue='2' checked={_theme['2']} id="radio2" type="radio" name="theme" />
            <label htmlFor="radio2"></label>
            <input onChange={fnChangeHandler} defaultValue='3' checked={_theme['3']} id="radio3" type="radio" name="theme" />
            <label htmlFor="radio3"></label>
          </div>
        </fieldset>
      </form>

      <nav>
        <Link to="/calc">CaLC</Link>
        <Link to="/bmi">BmI</Link>
        <Link to="/dday">DdAY</Link>
      </nav>

    </section>
  );
};

export default CompMenu;
