export const fnGetTheme = () => {
  let themeStorage = window.localStorage.getItem('themeStorage')
  let Theme

  if (themeStorage) {
    Theme = JSON.parse(window.localStorage.getItem('themeStorage', JSON.stringify(Theme)))

    if (Theme[1]) {
      Theme = 'gray'
    } else if (Theme[2]) {
      Theme = 'pink'
    } else {
      Theme = 'skyblue'
    }
    document.body.setAttribute('class', Theme)

  } else {
    Theme = { '1': true, '2': false, '3': false }
    window.localStorage.setItem('themeStorage', JSON.stringify(Theme))

  }
  return Theme
}
