import React from 'react';

const CompAdd = () => {
  return (
    <form className='dday-add'>
      <p>
        <label htmlFor="" placeholder=''><i className="fa-solid fa-pencil"></i>  D-day Title</label>
        <input type="text" />
      </p>
      <p>
      <label htmlFor=""><i className="fa-regular fa-calendar-days"></i>  D-day Date</label>
      <input type="date" />
      </p>
      <p>
      <label htmlFor=""><i className="fa-regular fa-clock"></i>  D-day Time</label>
      <input type="time" value={'00:00:00'} />
      </p>
      <button>추가하기</button>
    </form>
  );
};

export default CompAdd;
