import React, { useContext, useEffect, useState } from 'react';
import { BMIContext } from './CompBmi';
import { fnSetDegree, fnSetObj } from '../../js/bmi';
import CompMorph from './CompMorph';


const CompResult = () => {

  const { _active, _setActive, _setChkObj, _setCm, _setKg, _bmiResultObj, _gender } = useContext(BMIContext)

  let { bmi, bmiNum, bmiResult, fill } = _bmiResultObj
  const [_deg, _setDeg] = useState(0)



  /* 값이 변할때마다 변하도록 디그리?를 설정해줄거임 */
  useEffect(() => {
    if (_active) {
      let timeoutID
      clearTimeout(timeoutID)
      timeoutID = setTimeout(() => {
        _setDeg(fnSetDegree(_gender, bmi)/* 함수가 호출되어야 함 */)
        window.TweenMax.to(".morph .org", 1, { morphSVG: `.morph .${bmiNum}`, fill: fill, ease: window.Linear.easeNone })
      }, 500);
    }
  })

  const fnResetHandler = function () { // 결과창이 오른쪽으로 날아가게하는?
    _setActive('')
    _setChkObj(fnSetObj(0))
    _setCm('')
    _setKg('')
    _setDeg(0)
    bmiNum = 'man1'
    fill = '#8FACCA'
  }


  return (
    <div className={`bmi-result ${_active}`}>
      <h2>BMI Rerult</h2>
      <div className="output">
        <CompMorph />
        <figure className="board">
          <img src={require(`../../assets/img/bmiBoard.svg`).default} alt="" />
          <img className='pin' style={{ transform: `translate(7%,50%) rotate(${_deg}deg)` }} src={require(`../../assets/img/bmiPin.svg`).default} alt="" />
        </figure>
      </div> {/* output */}
      <button onClick={fnResetHandler}>
        <i className="fa-solid fa-rotate-right"></i> 다시 측정하기
      </button>
    </div>
  );
};

export default CompResult;
