  for (let i = 1; i <= 2; i++) {
    _chkObj[i] = false
  }
  _chkObj[n] = true
  return _chkObj
}

export const fnCheckBMI = (gender, cm, kg) => {
  const m = cm * 0.01//(m/100)
  let bmiResult = ''
