    } else if (bmi >= 20 && bmi < 25) {
      bmiResult = '정상'
      bmiNum = 'man3'
    } else if (bmi >= 25 && bmi < 30) {
      bmiResult = '조금 과함'
      bmiNum = 'man4'
    } else {
      bmiResult = '많이 과함'
      bmiNum = 'man5'
    } // male if else
  }

  /* 여자 bmi */
