      bmiResult = '저체중'
      bmiNum = 'woman2'
    } else if (bmi >= 25 && bmi < 30) {
      bmiResult = '정상'
      bmiNum = 'woman3'
    } else if (bmi >= 30 && bmi < 35) {
      bmiResult = '조금 과함'
      bmiNum = 'woman4'
    } else {
      bmiResult = '많이 과함'
      bmiNum = 'woman5'
    } // if else
  } // if female
  return { bmi, bmiNum, bmiResult } // {bmi:bmi, bmiNum:bmiNum, bmiResult:bmiResult}
}


