import React, { createContext, useState } from 'react';
import CompList from './CompList';

export const DDayContext = createContext()

const CompDday = () => {
  const [_showList, _setShowList] = useState()
  return (
    <DDayContext.Provider value={{
      _showList, _setShowList, //dday목록 화면과 dday추가화면 중에 하나를 보여주는 스테이트
    }}>
    <section className='dday'>
      <h2>D-DAY</h2>
      <CompList />
    </section>
    </DDayContext.Provider>
  );
};

export default CompDday;
