export const fnGetNow = function (dateObj) {
  // 현재 정보만 구해오는 애
  let now = dateObj // new Date() 
  let dayArr = ['sun', 'mon', 'tue', 'wed', 'thr', 'fri', 'sat',]
  let day = dayArr[now.getDay()]
  let year = now.getFullYear()
  let month = now.getMonth() + 1
  let date = now.getDate()
  let ap = (now.getHours() <= 12) ? 'AM' : 'PM'
  let hour = (now.getHours() <= 12) ? now.getHours() : now.getHours() - 12
  let min = now.getMinutes()
  let sec = now.getSeconds()

  return { now, day, year, month, date, ap, hour, min, sec }
}
