import React, { useContext } from 'react';
import { BMIContext } from './CompBmi';

const CompForm = () => {

  const {_setActive} = useContext(BMIContext)

  const fnSubmliHandler = function(e){
    e.preventDefault()
    _setActive(`active`)
  }

  return (
    <form onSubmit={fnSubmliHandler}>
      <div className='radios'>
        <label><i className="fa-solid fa-venus-mars"></i>성별을 입력하세요</label>
        <p>
          <input id="male" type="radio" name='gender' required />
          <label htmlFor="male"><i className="fa-solid fa-person"></i>male</label>
          <input id="female" type="radio" name='gender' required />
          <label htmlFor="female"><i className="fa-solid fa-person-dress"></i>female</label>
        </p>
      </div>
      <div>
        <label htmlFor="tall"><i className="fa-solid fa-ruler-vertical"></i>신장을 입력하세요</label>
        <input id="tall" type="number" required placeholder='cm단위로 입력해주세요' />
      </div>
      <div>
        <label htmlFor="weight"><i className="fa-solid fa-weight-scale"></i>체중을 입력하세요</label>
        <input id="tall" type="number" required placeholder='kg단위로 입력해주세요' />
      </div>
      <button><i class="fa-solid fa-rotate-right"></i> 다시 측정하기</button>
    </form>
  );
};

export default CompForm;
