import React, { useContext, useEffect, useState } from 'react';
import { BMIContext } from './CompBmi';
import { fnSetObj } from '../../js/bmi';

const CompResult = () => {

  const { _active, _setActive, _setChkObj, _setCm, _setKg, _bmiResultObj, _setBmiResultObj} = useContext(BMIContext)

  const {bmi, bmiNum, bmiResult} = _bmiResultObj
  const [_deg, _setDeg] = useState(0)
  const{Range, bmi1} = _bmiRange

  /* 값이 변할때마다 변하도록 디그리?를 설정해줄거임 */
  useEffect(() => {
    let timeoutID
    clearTimeout(timeoutID)
    timeoutID = setTimeout(() => {
      _setDeg(_bmiRange/* 함수가 호출되어야 함 */)
    }, 1000);
     /* 리턴에 아무값이 없으면 빈칸이 올려지지않을까? 라는 아무말 */
  })

  const fnResetHandler = function () { // 결과창이 오른쪽으로 날아가게하는?
    _setActive('')
    _setChkObj(fnSetObj(0))
    _setCm('')
    _setKg('')
  }


  return (
    <div className={`bmi-result ${_active}`}>
      <h2>BMI Rerult</h2>
      <div className="output">
      <figure className="board">
          <img src={require(`../../assets/img/bmiBoard.svg`).default} alt="" />
          <img className='pin' style={{transform : `translate(7%,50%) rotate(${_deg}deg)`}} src={require(`../../assets/img/bmiPin.svg`).default} alt="" />
        </figure>
      </div> {/* output */}
      <button onClick={fnResetHandler}>
        <i className="fa-solid fa-rotate-right"></i> 다시 측정하기
      </button>
    </div>
  );
};

export default CompResult;
