import React, { useContext, useState } from 'react';
import { DDayContext } from './CompDday';

const CompAdd = () => {
  const [_title, _setTitle] = useState('')
  const [_date, _setDate] = useState('')
  const [_time, _setTime] = useState('00:00')


  const {_showComp, _setShowComp} = useContext(DDayContext)

  const fnSubmitHandler = function (e) {
    e.preventDefault()
    _setShowComp('list')
  }

  return (
    <form onSubmit={fnSubmitHandler} className='dday-add'>

      <p>
        <label htmlFor="" ><i className="fa-solid fa-pencil"></i>  D-day Title</label>
        <input onChange={fnChangeHandler} type="text" placeholder='일정명을 입력해주세요' value={_title} />
      </p>

      <p>
        <label htmlFor=""><i className="fa-regular fa-calendar-days"></i>  D-day Date</label>
        <input onChange={fnChangeHandler} type="date" value={_date} />
      </p>

      <p>
        <label htmlFor=""><i className="fa-regular fa-clock"></i>  D-day Time</label>
        <input onChange={fnChangeHandler} type="time" value={_time} />
      </p>

      <button>추가하기</button>
    </form>
  );
};

export default CompAdd;
